import React, { useState } from 'react';
import './Calculator.css';

const Calculator = () => {
  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [result, setResult] = useState(null);

  const handleOperation = (op) => {
    const n1 = parseFloat(num1);
    const n2 = parseFloat(num2);
    if (isNaN(n1) || isNaN(n2)) {
      setResult('Invalid input');
      return;
    }
    let res;
    switch (op) {
      case '+':
        res = n1 + n2;
        break;
      case '-':
        res = n1 - n2;
        break;
      case '*':
        res = n1 * n2;
        break;
      case '/':
        res = n2 !== 0 ? n1 / n2 : 'Error: Division by zero';
        break;
      case '**':
        // Power using a for loop
        res = 1;
        for (let i = 0; i < n2; i++) {
          res *= n1;
        }
        break;
      default:
        res = 'Unknown operation';
    }
    setResult(res);
  };

  const clearCalculator = () => {
    setNum1('');
    setNum2('');
    setResult(null);
  };

  // Style the result text red if the numeric result is negative
  const resultStyle = {
    color: typeof result === 'number' && result < 0 ? 'red' : 'inherit'
  };

  return (
    <div className="calculator">
      <h3>Calculator</h3>
      <div className="inputs">
        <input
          type="text"
          value={num1}
          onChange={(e) => setNum1(e.target.value)}
          placeholder="First number"
        />
        <input
          type="text"
          value={num2}
          onChange={(e) => setNum2(e.target.value)}
          placeholder="Second number"
        />
      </div>
      <div className="buttons">
        <button onClick={() => handleOperation('+')}>+</button>
        <button onClick={() => handleOperation('-')}>-</button>
        <button onClick={() => handleOperation('*')}>*</button>
        <button onClick={() => handleOperation('/')}>/</button>
        <button onClick={() => handleOperation('**')}>**</button>
        <button onClick={clearCalculator}>Clear</button>
      </div>
      <div className="result">
        <h4 style={resultStyle}>Result: {result}</h4>
      </div>
    </div>
  );
};

export default Calculator;
