import React, { useState } from 'react';

const Calculator = () => {
  const [num1, setNum1] = useState("");
  const [num2, setNum2] = useState("");
  const [result, setResult] = useState("");

  const calculate = (operation) => {
    if (operation === 'clear') {
      setNum1("");
      setNum2("");
      setResult("");
      return;
    }
    const n1 = parseFloat(num1);
    const n2 = parseFloat(num2);
    let res;
    if (isNaN(n1) || isNaN(n2)) {
      setResult("Invalid input");
      return;
    }
    switch (operation) {
      case '+':
        res = n1 + n2;
        break;
      case '-':
        res = n1 - n2;
        break;
      case '*':
        res = n1 * n2;
        break;
      case '/':
        res = n2 !== 0 ? n1 / n2 : "Error";
        break;
      case '**':
        res = 1;
        for (let i = 0; i < n2; i++) {
          res *= n1;
        }
        break;
      default:
        res = "Unknown operation";
    }
    setResult(res);
  };

  const resultStyle = {
    color: (typeof result === 'number' && result < 0) ? 'red' : 'black'
  };

  return (
    <div className="calculator">
      <div className="inputs">
        <input
          type="number"
          id="num1"
          placeholder="Enter first number"
          value={num1}
          onChange={(e) => setNum1(e.target.value)}
        />
        <input
          type="number"
          id="num2"
          placeholder="Enter second number"
          value={num2}
          onChange={(e) => setNum2(e.target.value)}
        />
      </div>
      <div className="buttons">
        <button onClick={() => calculate('+')}>+</button>
        <button onClick={() => calculate('-')}>-</button>
        <button onClick={() => calculate('*')}>*</button>
        <button onClick={() => calculate('/')}>/</button>
        <button onClick={() => calculate('**')}>Power</button>
        <button onClick={() => calculate('clear')}>Clear</button>
      </div>
      <div className="result">
        <h3 id="output" style={resultStyle}>Result: {result}</h3>
      </div>
    </div>
  );
};

export default Calculator;
