import React, { useState } from 'react';

const Calculator = () => {
  const [num1, setNum1] = useState("");
  const [num2, setNum2] = useState("");
  const [result, setResult] = useState("");

  const calculate = (operation) => {
    if (operation === 'clear') {
      setNum1("");
      setNum2("");
      setResult("");
      return;
    }
    const n1 = parseFloat(num1);
    const n2 = parseFloat(num2);

    if (isNaN(n1) || isNaN(n2)) {
      setResult("Invalid input");
      return;
    }

    let res;
    switch (operation) {
      case '+':
        res = n1 + n2;
        break;
      case '-':
        res = n1 - n2;
        break;
      case '*':
        res = n1 * n2;
        break;
      case '/':
        res = (n2 !== 0) ? n1 / n2 : "Error";
        break;
      case '**':
        // power using a for loop
        res = 1;
        for (let i = 0; i < n2; i++) {
          res *= n1;
        }
        break;
      default:
        res = "Unknown operation";
    }
    setResult(res);
  };

  const resultStyle = {
    color: (typeof result === 'number' && result < 0) ? 'red' : 'black'
  };

  return (
    <div 
      className="calculator" 
      style={{
        background: "#fff",
        padding: "20px",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
        marginTop: "20px",
        maxWidth: "400px"
      }}
    >
      <div className="inputs" style={{ marginBottom: "15px" }}>
        <div style={{ margin: "10px 0" }}>
          <label htmlFor="num1" style={{ marginRight: "10px" }}>
            First Number:
          </label>
          <input
            type="number"
            id="num1"
            placeholder="Enter first number"
            value={num1}
            onChange={(e) => setNum1(e.target.value)}
          />
        </div>
        <div style={{ margin: "10px 0" }}>
          <label htmlFor="num2" style={{ marginRight: "10px" }}>
            Second Number:
          </label>
          <input
            type="number"
            id="num2"
            placeholder="Enter second number"
            value={num2}
            onChange={(e) => setNum2(e.target.value)}
          />
        </div>
      </div>

      <div className="buttons" style={{ marginBottom: "15px" }}>
        <button onClick={() => calculate('+')} style={{ marginRight: "5px" }}>+</button>
        <button onClick={() => calculate('-')} style={{ marginRight: "5px" }}>-</button>
        <button onClick={() => calculate('*')} style={{ marginRight: "5px" }}>*</button>
        <button onClick={() => calculate('/')} style={{ marginRight: "5px" }}>/</button>
        <button onClick={() => calculate('**')} style={{ marginRight: "5px" }}>Power</button>
        <button onClick={() => calculate('clear')}>Clear</button>
      </div>

      <div className="result">
        <h3 id="output" style={resultStyle}>Result: {result}</h3>
      </div>
    </div>
  );
};

export default Calculator;
